    #{item}

and isdelete=0

SELECT id, order_no AS orderNo, amount, spc_price AS spcPrice, real_amount AS realAmount, `status`, `type`, `pay_way` AS payWay, merchant_id AS merchantId, merchant_name AS merchantName, is_comment AS isComment from t_order AS o where o.user_id = #{userId}
and o.`status`=#{statu}
and o.`status` in(9,10,11)
and isdelete=0 order by o.create_date desc limit #{start}, #{size}

